mapper.insert:

INSERT INTO REST_ATTD VALUES (
  #{empCode}
, REST_ATTD_CODE_SEQ.NEXTVAL
, #{restTypeCode}
, #{restTypeName}
, TO_DATE(#{requestDate},'YY/MM/DD')
, TO_DATE(#{startDate},'YY/MM/DD')
, TO_DATE(#{endDate},'YY/MM/DD')
, #{cause}
, #{applovalStatus}
, #{rejectCause}
, TO_NUMBER(#{cost})
, TO_NUMBER(#{startTime})
, TO_NUMBER(#{endTime})
, TO_NUMBER(#{numberOfDays})
)

mapper.update:
		UPDATE REST_ATTD SET 
      APPLOVAL_STATUS = #{applovalStatus},
      REJECT_CAUSE = #{rejectCause}
      WHERE EMP_CODE = #{empCode} 
      AND REQUEST_DATE = TO_DATE(#{requestDate},'YYYY-MM-DD')

mapper.delete:
DELETE FROM REST_ATTD 
WHERE EMP_CODE = #{empCode}
